def get_teaching_slots(magic_code):
    """Get all teaching slots for a teacher."""
    return TIMETABLE_SLOTS.get(magic_code, [])


def get_home_room(magic_code):
    """Get home room for a teacher (None for floaters)."""
    return STAFF_HOME_ROOMS.get(magic_code)


def is_floater(magic_code):
    """Check if teacher is a floater (no home room)."""
    return STAFF_HOME_ROOMS.get(magic_code) is None


def get_busy_slots(magic_code):
    """Get set of (day, period) tuples when teacher is busy."""
    return {(d, p) for d, p, *_ in TIMETABLE_SLOTS.get(magic_code, [])}


def get_room_occupancy(venue_code):
    """Get all (day, period) tuples when a room is occupied."""
    occupancy = set()
    for slots in TIMETABLE_SLOTS.values():
        for d, p, subj, gr, cls, venue in slots:
            if venue == venue_code:
                occupancy.add((d, p))
    return occupancy
